(function() {
  var save_alkane_standards;

  $(window).load(function() {
    $('#preprocessing').on('shown.bs.modal', '.alkane-dialog', function() {
      return $('#alkane-standard-input').focus();
    });
    $('#preprocessing').on('click', '#alkane-save', function() {
      var label, label_id;
      if (/^\s*C\d+\s*$/.test($('#alkane-standard-input').val())) {
        label_id = $('.alkane-dialog').data('label-id');
        label = window.alkane_sv.annotation.labels.get(label_id);
        label.text = $('.alkane-dialog #alkane-standard-input').val().replace(/^\s+|\s+$/g, '');
        window.alkane_sv.draw();
        return save_alkane_standards();
      }
    });
    return $('#preprocessing').on('keyup', '#alkane-standard-input', function() {
      if (/^\s*C\d+\s*$/.test($(this).val())) {
        $('#alkane-save').removeClass('disabled');
        $('~ span', this).addClass('glyphicon-ok').removeClass('glyphicon-remove');
        $('.alkane-group').addClass('has-success').removeClass('has-error');
        return $('#alkane-message').html('');
      } else {
        $('#alkane-save').addClass('disabled');
        $('~ span', this).addClass('glyphicon-remove').removeClass('glyphicon-ok');
        $('.alkane-group').addClass('has-error').removeClass('has-success');
        return $('#alkane-message').html('Should be C+number (e.g. C12)');
      }
    });
  });

  save_alkane_standards = function() {
    var data, submission_id;
    data = window.alkane_sv.annotation.labels.map(function(label) {
      return {
        x: label.x,
        y: label.y,
        text: label.text
      };
    });
    submission_id = $('#alkane-viewer').data('submission-id');
    return $.ajax({
      url: '/submissions/' + submission_id + '/save_alkane_standards.json',
      type: 'POST',
      data: {
        alkane_standards: JSON.stringify(data)
      },
      dataType: 'text',
      success: function() {
        return $('.alkane-dialog').modal('hide');
      },
      error: function(jqXHR, status, error) {
        return alert('There was a problem saving the adjustments: ' + error);
      }
    });
  };

}).call(this);
(function() {
  $(function() {
    var check_custom_list_count, custom_db_count, db_table, dialog_for, update_custom_list_count;
    db_table = $('.db-list').DataTable({
      "scrollCollapse": true,
      "scrollY": "300px",
      "paging": false,
      "destroy": true,
      "columnDefs": [
        {
          "targets": 0,
          "orderable": false
        }
      ],
      "order": [[1, 'asc']]
    });
    $('.custom-db-list').on('shown.bs.modal', function() {
      return $('.db-list').DataTable().columns.adjust().draw();
    });
    $('.tool-tip').tooltip();
    $('.btn-disabled').on('click', function() {
      return false;
    });
    dialog_for = function(element) {
      return $(element).parents('.custom-db-list');
    };
    $('.custom-db-edit').on('click', function() {
      var db, dialog;
      db = $(this).attr('href');
      dialog = $('.custom-db-list[data-db=' + db + ']');
      if (custom_db_count(dialog) === 0) {
        $('.db-button-all', dialog).trigger('click');
      }
      dialog.modal('show');
      return false;
    });
    $('.custom-db-cancel').on('click', function() {
      return $(dialog_for(this)).modal('hide');
    });
    $('.custom-db-save').on('click', function() {
      var dialog;
      if ($(this).hasClass('disabled')) {
        return;
      }
      dialog = dialog_for(this);
      if ($('input:checkbox:checked', dialog).length === $('input:checkbox', dialog).length) {
        $('.db-button-none', dialog).trigger('click');
      }
      $('input:checkbox', dialog).each(function() {
        return $(this).data('checked-state', this.checked);
      });
      $('#radio-database-' + dialog.data('db')).prop('checked', true).change();
      return $(dialog).modal('hide');
    });
    $('.custom-db-list').on('hide.bs.modal', function() {
      $('.custom-db-list input:checkbox').each(function() {
        return $(this).prop('checked', $(this).data('checked-state'));
      });
      return check_custom_list_count($(this));
    });
    $('.db-button-all').on('click', function() {
      $('input:checkbox', dialog_for(this)).prop('checked', true).change();
      return false;
    });
    $('.db-button-none').on('click', function() {
      $('input:checkbox', dialog_for(this)).prop('checked', false).change();
      return false;
    });
    $('.custom-db-list input:checkbox').on('change', function() {
      return update_custom_list_count(dialog_for(this));
    });
    check_custom_list_count = function(dialog) {
      var db;
      db = dialog.data('db');
      return $('.custom-db-edit[href="' + db + '"]').toggleClass('db-edited', custom_db_count(dialog) > 0);
    };
    update_custom_list_count = function(dialog) {
      var checked_count;
      checked_count = custom_db_count(dialog);
      $('.custom-db-count', dialog).html(checked_count);
      return $('.custom-db-save', dialog).toggleClass('disabled', checked_count === 0);
    };
    custom_db_count = function(dialog) {
      return $('input:checked', dialog).length;
    };
    return $('#new_submission').submit(function() {
      return $('#database-selection input:not(:checked)').each(function() {
        return $('.custom-db-list[data-db="' + $(this).val() + '"] input:checkbox').prop('checked', false);
      });
    });
  });

}).call(this);
