    // this.svg.style('cursor', 'crosshair');

    // Draw Border
    this.border = this.svg.append('rect')
      .attr('x', 0)
      .attr('y', 0)
      .attr('width', this.width)
      .attr('height', this.height)
      .attr('fill', 'white')
      .attr('stroke', '#BBB');

